import React from 'react';

import {
  FriendsListLi,
  FriendsStatus
} from './FriendList.styled';

export const FriendListItem = ({ avatar, name, isOnline }) => {
  return (
    <FriendsListLi>
      <FriendsStatus $online={isOnline}></FriendsStatus>
      <img src={avatar} alt="User avatar" width="48" />
      <p>{name}</p>
    </FriendsListLi>
  );
};
